export class Uint8ArrayConsumer {
  static fromHexString(hex: string) {
    const lowHex = hex.toLowerCase();
    if (/^(([a-f]|\d){2})*$/.test(lowHex)) {
      const arr = new Uint8Array(
        (lowHex.match(/([a-z]|\d){2}/g) || []).map((byte) => parseInt(byte, 16))
